CREATE TABLE IF NOT EXISTS users (
                                     chat_id BIGINT PRIMARY KEY, -- Unique chat ID for each user
                                     language VARCHAR(10), -- User's language preference, default to 'en'
                                     first_name VARCHAR(50),
                                     last_name VARCHAR(50),
                                     username VARCHAR(50)
);
drop table users;
SELECT * FROM users;
CREATE TABLE contacts (
                          lang TEXT PRIMARY KEY, -- Language code ('ru', 'tg', etc.)
                          info TEXT NOT NULL       -- Contact information text (HTML formatted)
);

