INSERT INTO directory_menu (command, answer, language, category) VALUES
                                                       ('Что означает система электронных заявлений?', 'Электронная система подачи заявлений — информационная система, используемая заявителем для подачи заявления о выдаче, переоформлении, прекращении действия, выдаче второго экземпляра (дубликата) лицензии, разрешительного документа, а также для прохождения разрешительных и согласований, направления уведомлений в электронно-цифровой форме, в прямом (электронном) режиме, и является составной частью государственной системы выдачи лицензий, разрешительных документов, разрешительных и согласований, направления уведомлений.', 'ru', 'ijozatnoma'),
                                                       ('Что такое лицензия?', 'Лицензия — специальный распорядительный документ разрешительного характера, выдаваемый лицензирующим органом субъекту хозяйствования на осуществление отдельных видов деятельности или действий, связанных с повышенной степенью риска, с соблюдением лицензионных условий, квалификационных требований и условий.', 'ru', 'ijozatnoma'),
                                                       ('Какие документы необходимы для получения лицензии?', 'Документы, необходимые для получения лицензии:\n1) заявление о выдаче лицензии с указанием вида деятельности или действия;\n2) копия документа, подтверждающего государственную регистрацию субъекта предпринимательской деятельности;\n3) документ, подтверждающий уплату регистрационного сбора;\n4) доверенность на право представления интересов, если документы подает представитель.\nМогут потребоваться иные документы в зависимости от специфики деятельности.', 'ru', 'ijozatnoma'),
                                                       ('Что мне делать, если срок действия моего разрешения истек?', 'Обратитесь в Государственную службу по надзору в сфере транспорта и при необходимости подайте заявление на получение нового разрешения. Тел: 488888118.', 'ru', 'ijozatnoma'),
                                                       ('Каковы обязанности по осуществлению деятельности без необходимой лицензии?', 'Наказание предусмотрено в соответствии со статьей 510 Кодекса Республики Таджикистан об административных правонарушениях. Также за нарушение условий лицензии применяются штрафы по статье 511.', 'ru', 'ijozatnoma'),
                                                       ('Каков срок действия разрешительных документов?', '1) Лицензии и разрешительные документы, кроме лицензий на недропользование, выдаются бессрочно.\n2) Лицензии на недропользование — по сроку, определенному Правительством.\n3) Разовые документы действуют до завершения действия.\n4) Акты согласования и уведомления — разовые и не имеют срока действия.', 'ru', 'ijozatnoma'),
                                                       ('Что означает получение предписания?', 'Прием предписания — разовое административное действие, совершаемое государственным органом для уведомления о начале определенной деятельности.', 'ru', 'ijozatnoma'),
                                                       ('Контакты', 'Тел: 488888118.', 'ru', 'ijozatnoma'),
                                                       ('Низоми электронӣ барои додани ариза чи маъно дорад ?', 'Низоми электронӣ барои додани ариза — низоми иттилоотие мебошад, ки аризадиҳанда онро барои пешниҳоди ариза дар бораи додан, аз нав расмӣ намудани, қатъ кардани, гирифтани нусхаи дуюм (дубликат) иҷозатнома, ҳуҷҷати иҷозатдиҳӣ, инчунин барои гузаштани расмиёти тасдиқ ва мувофиқасозӣ ва ирсоли огоҳиномаҳо дар шакли рақамӣ истифода мебарад.', 'tg', 'ijozatnoma'),
                                                       ('Иҷозатнома чист ?', 'Иҷозатнома — ҳуҷҷати расмии дорои хусусияти иҷозатдиҳӣ мебошад, ки онро мақоми иҷозатдиҳанда ба субъекти хоҷагидор барои иҷрои фаъолият ё амалҳои муайян, ки бо хавфи баланд алоқаманданд, бо риояи талабот ва шартҳои тахассусӣ медиҳад.', 'tg', 'ijozatnoma'),
                                                       ('Ҳуҷҷатҳои зарурӣ барои гирифтани иҷозатнома кадомҳоянд ?', 'Барои гирифтани иҷозатнома, шахс бояд ҳуҷҷатҳои зеринро ба мақоми иҷозатдиҳанда пешниҳод намояд:\n1) Ариза бо зикри намуди фаъолият;\n2) Нусхаи ҳуҷҷати бақайдгирии давлатӣ;\n3) Ҳуҷҷати пардохти хироҷ;\n4) Ваколатнома, агар намоянда пешниҳод кунад.\nМумкин аст ҳуҷҷатҳои иловагӣ талаб шаванд.', 'tg', 'ijozatnoma'),
                                                       ('Агар муҳлати рухсатнома гузарад, чӣ бояд кард?', 'Ба Хадамоти назорати давлатии соҳаи нақлиёт муроҷиат намоед ва агар лозим шавад, барои гирифтани иҷозатномаи нав дархост диҳед.', 'tg', 'ijozatnoma'),
                                                       ('Бе иҷозатномаи зарурӣ фаъолият намудан чи ҷавобгарӣ дорад?', 'Мувофиқи моддаи 510-и Кодекси ҳуқуқвайронкунии маъмурии Ҷумҳурии Тоҷикистон, барои фаъолият бе иҷозатнома ҷазо муқаррар шудааст. Ҳамчунин барои вайрон кардани шартҳои иҷозатнома ҷазо дар моддаи 511 пешбинӣ шудааст.', 'tg', 'ijozatnoma'),
                                                       ('Муҳлати амали ҳуҷҷатҳои иҷозатдиҳӣ чанд аст ?', '1) Иҷозатномаҳо (ғайр аз истифодаи сарватҳои зеризаминӣ) ба муҳлати номаҳдуд дода мешаванд.\n2) Иҷозатномаҳои якдафъаина то анҷоми амал эътибор доранд.\n3) Амали иҷозатнома бо асосҳои қонун қатъ мегардад.\n4) Санадҳои тасдиқ ва огоҳиномаҳо якдафъаина буда, муҳлат надоранд.', 'tg', 'ijozatnoma'),
                                                       ('Гирифтани огоҳинома чи маъно дорад ?', 'Гирифтани огоҳинома — амали якдафъаинаи мақомоти давлатӣ мебошад, ки огоҳинома ва ҳуҷҷатҳои замимашударо қабул мекунад ва аз ҷониби субъектҳои соҳибкорӣ барои огоҳ намудани давлат пешниҳод мегардад.', 'tg', 'ijozatnoma'),
                                                       ('Телефон', 'Тел: 488888118.', 'tg', 'ijozatnoma');


INSERT INTO directory_menu (command, answer, category, language) VALUES
                                                                     ('Борхати молию нақлиётӣ чист?', 'Борхати молию нақлиётӣ - ҳуҷҷате, ки дорои маълумот оид ба арзишҳои молию моддии бор буда, барои баҳисобгирӣ ва назорати ҳаракати онҳо ва тасдиқи далели амалӣ шудани ҳамлу нақл, аз ҷумла барои бастани шартномаи ҳамлу нақли бор пешбинӣ гардидааст;', 'borhat', 'tg'),
                                                                     ('Вазни бор дар борхат нишон дода мешавад ?', 'Бале, дар борхати молию наѕлиётњ (борхат) ё наряди автомобилњ борфиристанда ё экспедитор вазни борро бо нишон додани тарзи муайян кардани он зикр мекунад.', 'borhat', 'tg'),
                                                                     ('Борхати электронӣ чи гуна аст ?', 'Борхатҳои электронӣ метавонанд ҳамаи маълумотҳои, ки дар варақаҳои борхати шаклҳои муқаррарӣ оварда шудаанд, дар онҳо тариқи иттилооти электронӣ ворид карда мешаванд. Дар қисми поёни кунҷи чапи роҳхатҳо шакли “QR-code” барои амалӣ намудани назорати мақомоти дахлдор гузошта шуда, дар он маълумоти зарурӣ оид ба фаъолияти воситаи нақлиёти автомобилии ҳамлу нақли бору мусофирон ва бағоҷ ва ронанда нишон дода мешавад.', 'borhat', 'tg'),
                                                                     ('Ронанда ҳангоми фаъолият борхатро ба кадом мақомотҳо пешкаш мекунад ?', 'Ронанда роҳхатро ҳамнгоми фаъолият бо худ нигоҳ дошта, бо талаби кормандони МД “Нақлиёти автомобилӣ ва хизматрасонии логистикӣ”, Хадамоти давлатии назорат ва танзим дар соҳаи нақлиёт ва Бозрасии давлатии автомобилии Вазорати корҳои дохилии Ҷумҳурии Тоҷикистон пешкаш менамояд.', 'borhat', 'tg'),
                                                                     ('Борхатро аз куҷо дастрас намудан мумкин ?', 'Ронандагон борхатро дар асоси шартнома аз ширкатҳое, ки ба ҳамлу нақли автомобилӣ машғуланд, дастрас намуда метавонанд. Дар мавридҳои зарурӣ метавонанд ба МД “Нақлиёти автомобилӣ ва хизматрасонии логистикӣ” муроҷиат намоянд.', 'borhat', 'tg'),
                                                                     ('Бе борхат фаъолият намудан чи ҷавобгарӣ дорад?', 'Мувофиқи моддаи 511 Кодекси ҳуқуқвайронкунии маъмурии Ҷумҳурии Тоҷикистон барои вайрон намудани шарту талаботи иҷозатнома аз тарафи мақомоти ваколатдор ҷазо татбиқ карда мешавад.', 'borhat', 'tg'),
                                                                     ('Телефон','Тел:2222056.', 'borhat', 'tg'),
                                                                     ('Что такое Товарно-транспортная накладная?', 'Товарно-транспортная накладная — документ, содержащий сведения о материальных и денежных ценностях груза, предназначенный для учета и контроля за их перемещением и подтверждения факта перевозки, в том числе для заключения договора перевозки груза;', 'borhat', 'ru'),
                                                                     ('Указан ли вес груза в накладной?', 'Да, в транспортной накладной или заказе на перевозку груза грузоотправитель или экспедитор указывает вес груза, указывая, как он был определен.', 'borhat', 'ru'),
                                                                     ('Что такое электронный накладной?', 'Электронные накладные могут содержать всю информацию, содержащуюся в стандартных накладных, введенную в электронном виде. В левом нижнем углу билетов для контроля соответствующими органами размещается «QR-код», на котором отображается необходимая информация о работе транспортного средства, перевозящего пассажиров и багаж, а также о водителе.', 'borhat', 'ru'),
                                                                     ('В какие органы водитель предъявляет товарно-транспортную накладную во время работы?', 'Водитель имеет при себе разрешение в течение всей своей деятельности и предъявляет его по требованию сотрудников Государственного агентства автомобильного транспорта и логистических услуг, Государственной службы по надзору и регулированию в сфере транспорта и Государственной автомобильной инспекции Министерства внутренних дел Республики Таджикистан.', 'borhat', 'ru'),
                                                                     ('Где я могу получить накладную?', 'Водители могут получить товарно-транспортную накладную на основании договора с компаниями, осуществляющими автомобильные перевозки. В необходимых случаях они могут обратиться в Государственное агентство автомобильного транспорта и логистических услуг.', 'borhat', 'ru'),
                                                                     ('Каковы обязанности по осуществлению деятельности без накладной?', 'В соответствии со статьей 511 Кодекса Республики Таджикистан об административных правонарушениях за нарушение условий и требований лицензии уполномоченным органом налагаются штрафные санкции.', 'borhat', 'ru'),
                                                                     ('Контакты', 'Тел: 2222056.', 'borhat', 'ru');


INSERT INTO directory_menu (command, answer, category, language) VALUES
-- Russian entries
('Что такое Товарно-транспортная накладная?', 'Товарно-транспортная накладная — документ, содержащий сведения о материальных и денежных ценностях груза, предназначенный для учета и контроля за их перемещением и подтверждения факта перевозки, в том числе для заключения договора перевозки груза;', 'permit', 'ru'),
('Кто может получить международное разрешение?', 'Разрешения выдаются физическим и юридическим лицам Республики Таджикистан, занимающимся международными перевозками.', 'permit', 'ru'),
('Какие документы необходимы для получения разрешения?', 'Документы, необходимые в соответствии с законодательством и международными договорами Республики Таджикистан для осуществления международных автомобильных перевозок, должны находиться у водителей автотранспортных средств: заграничный паспорт водительский удостоверения международного образца книжка TIR-CARNET талон обязательного государственного технического осмотра транспортного средства свидетельство о регистрации транспортного средства свидетельство о допуске автотранспортного средства к международной перевозке грузов под таможенными печатями и пломбами товарно-транспортная накладная в соответствии с Конвенцией о договоре по перевозке грузов свидетельство о допуске транспортного средства к перевозке скоропортящихся грузов транспортная виза разрешение на въезд и транзит страховой полис', 'permit', 'ru'),
('Каков процесс получения разрешения?', 'Процесс включает: • Подачу заявки, • Рассмотрение документов, • Подтверждение соответствия, • Выдачу разрешения на основе квоты.', 'permit', 'ru'),
('Что делать, если срок действия разрешения истек?', 'Обратитесь в Службу государственного контроля в сфере транспорта и при необходимости подайте заявление на получение нового разрешения.', 'permit', 'ru'),
('Какие органы осуществляют контроль за въездом, выдачей и транзитом транспорта?', 'Следующие органы: • Пограничный контроль (паспорт и виза), • Таможня, • Транспортные органы, • Фитосанитарные и ветеринарные службы.', 'permit', 'ru'),
('Можно ли передавать разрешение от одного перевозчика другому?', 'Нет, это строго запрещено.', 'permit', 'ru'),
('Какие данные содержит бланк разрешения?', 'Бланки разрешения должны содержать следующие реквизиты, размещаемые на лицевой стороне бланка:\n- вид перевозки, право на осуществление которого подтверждает разрешительный документ;\n- серия и номер;\n- количество разрешаемых поездок;\n- срок действия;\n- реквизиты перевозчика, которому выдается разрешительный бланк;\n- место выдачи разрешительного бланка;\n- подпись и печать компетентного органа, выдавшего разрешительный бланк;\n- маршрут движения по территории Республики Таджикистан;\n- данные о подвижном составе;\n- данные о перевозимом грузе;\n- снаряжённая масса автотранспортного средства без груза;\n- место для отметок и печати контролирующих органов в пути следования;\n- основные предписания по использованию разрешительного бланка.', 'permit', 'ru'),
('Какие существуют виды разрешений?', 'Существуют различные формы, в том числе:\nВиды разрешений и используемых соглашений;\n- разрешение на въезд и выезд иностранных автотранспортных средств (Форма Р-1);\n- разрешение на транзитный проезд иностранных автотранспортных средств (Форма Р-2);\n- разрешение на ввоз и вывоз грузов в/из третьих стран отечественными и иностранными автомобильными перевозчиками (Форма Р-3);\n- разрешение на бесплатный въезд и выезд иностранных автотранспортных средств для обмена, согласно подписанных межправительственных соглашений (Форма Р-БП);\n- разрешение на въезд, выезд и транзит опасных грузов (СР-1);\n- разрешение на перевозку крупногабаритных и тяжеловесных грузов (Форма СР-2);\n- разрешение на перевозку скоропортящихся грузов (Форма - СР-3);\n- разрешительный бланк (Форма Р-3 - ОГ) предназначен для вывоза грузов и пассажиров иностранными автотранспортными средствами из Республики Таджикистан следующих в обратном направлении в свои страны;', 'permit', 'ru'),
('Что включает в себя международная перевозка в Таджикистане?', '• Въезд и выезд транспортных средств, • Внутренний и внешний транзит, • Перевозка различных грузов, включая опасные.', 'permit', 'ru'),
('Какие документы регулируют порядок движения?', '• Международные соглашения, • Правила Устава автомобильного транспорта, • Положение о порядке лицензирования.', 'permit', 'ru'),
('Какие транспортные средства могут использоваться в международных перевозках?', '- грузовые автомобили всех марок и грузоподъемностей;\n- автобусы и автомобили, предназначенные для перевозки пассажиров (имеющие не менее 7-ми сидячих мест, кроме места водителя);\n- легковые автотранспортные средства всех марок и модификаций.', 'permit', 'ru'),
('Какая ответственность предусмотрена за деятельность без соответствующего разрешения?', 'В соответствии со статьёй 510 Кодекса об административных правонарушениях Республики Таджикистан предусмотрены наказания. Также за нарушение условий и требований разрешения по статье 511 применяются санкции со стороны уполномоченных органов.', 'permit', 'ru'),
('Каков срок действия разрешения?', 'Разрешения на международные автомобильные перевозки выдаются и действительны на одну поездку.', 'permit', 'ru'),
('Телефон', 'Тел: 488888118', 'permit', 'tg');

INSERT INTO directory_menu (command, answer, category, language) VALUES
                                                                     ('Барои чӣ ба рухсатномаи байналмилалии нақлиётӣ ниёз дорем?', 'Рухсатномаи байналмилалӣ барои интиқоли бор ва мусофирон байни кишварҳо зарур аст. Он бар асоси созишномаҳои байналмилалӣ ва квотаҳои тасдиқшуда бо кишварҳои хориҷӣ дода мешавад.', 'permit', 'tg'),
                                                                     ('Кӣ метавонад рухсатномаи байналмилалӣ гирад?', 'Рухсатномаҳо ба шахсони воқеӣ ва ҳуқуқии Ҷумҳурии Тоҷикистон дода мешаванд, ки ба ҳамлу нақли байналмилалӣ машғуланд.', 'permit', 'tg'),
                                                                     ('Барои гирифтани рухсатнома чӣ ҳуҷҷатҳо лозиманд?', 'Ҳуҷҷатҳое, ки тибқи қонунгузорӣ ва шартномаҳои байналмилалии Ҷумҳурии Тоҷикистон барои иҷрои ҳамлу нақли байналмилалии автомобилӣ заруранд, бояд дар дасти ронандагони воситаҳои нақлиёти автомобилӣ мавҷуд бошанд:\nшиносномаи хориҷӣ;\nшаҳодатномаи ронандагӣ (мувофиқ ба намунаҳои байналмилалӣ);\nкитобчаи TIR-CARNET;\nшаҳодатномаи азназаргузаронии техникии давлатӣ (хатмӣ) барои воситаи нақлиёт;\nшаҳодатномаи бақайдгирии воситаи нақлиёт;\nмаълумот оид ба роҳ додани воситаи нақлиёти автомобилӣ ба боркашонии байналмилалии автомобилӣ бо мӯҳр ва тамғаи гумрукӣ;\нборхати молию нақлиётӣ (мутобиқи Конвенсия дар бораи шартнома оид ба ҳамлу нақли борҳо);\nшаҳодатнома дар бораи иҷозат додани воситаи нақлиёт ба ҳамлу нақли борҳои зудвайроншаванда;\нраводиди нақлиётӣ;\nиҷозат барои вуруд ва транзит;\nсуғуртанома.', 'permit', 'tg'),
                                                                     ('Раванди гирифтани рухсатнома чӣ гуна аст?', 'Раванд чунин аст:\n• Пешниҳоди дархост,\n• Баррасии ҳуҷҷатҳо,\n• Тасдиқи мувофиқат,\n• Додани рухсатнома бар асоси квота.', 'permit', 'tg'),
                                                                     ('Агар муҳлати рухсатнома гузарад, чӣ бояд кард?', 'Ба Хадамоти назорати давлатии соҳаи нақлиёт муроҷиат кунед ва дар ҳолати зарурӣ барои гирифтани рухсатномаи нав дархост диҳед.', 'permit', 'tg'),
                                                                     ('Назорати вуруд, судур ва транзити нақлиёт тавассути кадом мақомот сурат мегирад?', 'Мақомотҳои зерин:\n• Назорати сарҳадӣ (шиноснома ва раводид),\n• Гумрук,\n• Мақомоти нақлиёт,\n• Мақомоти фитосанитарӣ ва байторӣ.', 'permit', 'tg'),
                                                                     ('Оё додани рухсатнома аз як интиқолдиҳанда ба дигар мумкин аст?', 'Не, ин қатъиян манъ аст.', 'permit', 'tg'),
                                                                     ('Варақаи рухсатнома кадом маълумотро дар бар мегирад?', 'Варақаҳои рухсатнома бояд дорои чунин мушаххасоти дар рӯи варақа зикргардида бошад:\n- намуди ҳамлу нақл;\n- силсила ва рақам;\n- миқдори сафарҳои иҷозатдодашаванда;\n- муҳлати амал;\n- мушаххасоти интиқолдиҳанда;\n- ҷои додани рухсатнома;\n- имзо ва муҳри мақомот;\n- хатсайри ҳаракат тавассути Ҷумҳурии Тоҷикистон;\n- маълумот дар бораи воситаи нақлиёт;\n- маълумот дар бораи бор;\n- вазни иҷозатдодашуда бе бор;\n- ҷой барои қайд ва муҳри мақомот;\n- тавсияҳо оид ба истифода.', 'permit', 'tg'),
                                                                     ('Намудҳои рухсатномаҳо кадомҳоянд?', 'Шаклҳои гуногун мавҷуданд, аз ҷумла:\n- Р-1,\n- Р-3,\n- Р-БП,\n- СР-1,\n- СР-2,\n- СР-3,\n- Р-З-ОГ.', 'permit', 'tg'),
                                                                     ('Ҳамлу нақли байналмилалӣ дар Тоҷикистон чӣ корҳоро дар бар мегирад?', '• Вуруд ва баромади воситаҳои нақлиёт,\n• Транзити дохилӣ ва хориҷӣ,\n• Интиқоли борҳои гуногун аз ҷумла борҳои хатарнок', 'permit', 'tg'),
                                                                     ('Тартиби рафтуомад бо кадом санадҳо танзим мешавад?', '• Шартномаҳои байналмилалӣ,\n• Қоидаҳои Оинномаи нақлиёти автомобилӣ,\n• Низомнома дар бораи хусусиятҳои иҷозатномадиҳӣ.', 'permit', 'tg'),
                                                                     ('Кадом воситаҳои нақлиёт метавонанд дар ҳамлу нақли байналмилалӣ истифода шаванд?', '- автомобилҳои боркашонии ҳама гуна тамға ва борбардорӣ;\n- автобус ва воситаҳои нақлиёти мусофиркаш (на камтар аз 7 ҷой);\n- нақлиёти сабукрави ҳама гуна шакл.', 'permit', 'tg'),
                                                                     ('Бе иҷозатномаи зарурӣ фаъолият намудан чи ҷавобгарӣ дорад?', 'Мувофиқи моддаҳои 510 ва 511 Кодекси ҳуқуқвайронкунии маъмурии Ҷумҳурии Тоҷикистон ҷазо пешбинӣ шудааст.', 'permit', 'tg'),
                                                                     ('Муҳлати амалии рухсатнома чӣ гуна аст?', 'Рухсатномаҳо барои як рафтуомад эътибор доранд.', 'permit', 'tg'),
                                                                     ('Телефон', 'Тел: 488888118', 'permit', 'tg');

select * from directory_menu;


INSERT INTO directory_menu (command, answer, category, language) VALUES
-- Russian entries
('Зачем нужен международный транспортный разрешительный документ?', 'Международное разрешение необходимо для перевозки грузов и пассажиров между странами. Оно выдается на основе международных соглашений и утверждённых квот с зарубежными государствами.', 'permit', 'ru'),
('Кто может получить международное разрешение?', 'Разрешения выдаются физическим и юридическим лицам Республики Таджикистан, занимающимся международными перевозками.', 'permit', 'ru'),
('Какие документы необходимы во время поездки для водителя?', 'Документы, необходимые в соответствии с законодательством и международными договорами Республики Таджикистан для осуществления международных автомобильных перевозок, должны находиться у водителей автотранспортных средств: заграничный паспорт, водительское удостоверение международного образца, книжка TIR-CARNET, талон обязательного техосмотра, свидетельство о регистрации ТС, свидетельство о допуске к международной перевозке, товарно-транспортная накладная, свидетельство о допуске к перевозке скоропортящихся грузов, транспортная виза, разрешение на въезд и транзит, страховой полис.', 'permit', 'ru'),
('Каков процесс получения разрешения?', 'Процесс включает: • Подачу заявки, • Рассмотрение документов, • Подтверждение соответствия, • Выдачу разрешения на основе квоты.', 'permit', 'ru'),
('Что делать, если срок действия разрешения истек?', 'Обратитесь в Службу государственного контроля в сфере транспорта и при необходимости подайте заявление на получение нового разрешения.', 'permit', 'ru'),
('Какие органы осуществляют контроль за въездом, выдачей и транзитом транспорта?', 'Следующие органы: • Пограничный контроль (паспорт и виза), • Таможня, • Транспортные органы, • Фитосанитарные и ветеринарные службы.', 'permit', 'ru'),
('Можно ли передавать разрешение от одного перевозчика другому?', 'Нет, это строго запрещено.', 'permit', 'ru'),
('Какие данные содержит бланк разрешения?', 'Бланки разрешения должны содержать: вид перевозки, серия и номер, количество поездок, срок действия, данные перевозчика, место выдачи, подпись и печать органа, маршрут по территории РТ, данные о подвижном составе и грузе, снаряжённая масса ТС, место для отметок и предписания по использованию.', 'permit', 'ru'),
('Какие существуют виды разрешений?', 'Существуют различные формы:\n- Р-1: въезд/выезд иностранных ТС\n- Р-2: транзит иностранных ТС\n- Р-3: ввоз/вывоз грузов в/из третьих стран\n- Р-БП: бесплатный въезд/выезд\n- СР-1: опасные грузы\n- СР-2: крупногабаритные грузы\n- СР-3: скоропортящиеся грузы\n- Р-3-ОГ: вывоз из РТ в обратном направлении.', 'permit', 'ru'),
('Что включает в себя международная перевозка в Таджикистане?', '• Въезд и выезд транспортных средств\n• Внутренний и внешний транзит\n• Перевозка различных грузов, включая опасные.', 'permit', 'ru'),
('Какие документы регулируют порядок движения?', '• Международные соглашения\n• Правила Устава автотранспорта\n• Положение о лицензировании.', 'permit', 'ru'),
('Какие транспортные средства могут использоваться в международных перевозках?', '• Грузовые автомобили всех марок\n• Автобусы и автомобили с ≥7 сидячими местами (кроме водителя)\n• Легковые автомобили всех марок.', 'permit', 'ru'),
('Какая ответственность предусмотрена за деятельность без разрешения?', 'В соответствии со статьями 510 и 511 Кодекса об административных правонарушениях РТ, за отсутствие разрешения или нарушение условий применяются санкции.', 'permit', 'ru'),
('Каков срок действия разрешения?', 'Разрешения на международные автомобильные перевозки выдаются и действительны на одну поездку.', 'permit', 'ru'),
('Контакты', 'Тел:488888118', 'permit', 'ru');


INSERT INTO directory_menu (command, answer, category, language) VALUES
                                                                     ('Барои чӣ ба рухсатномаи байналмилалии нақлиётӣ ниёз дорем?', 'Рухсатномаи байналмилалӣ барои интиқоли бор ва мусофирон байни кишварҳо зарур аст. Он бар асоси созишномаҳои байналмилалӣ ва квотаҳои тасдиқшуда бо кишварҳои хориҷӣ дода мешавад.', 'permit', 'tg'),
                                                                     ('Кӣ метавонад рухсатномаи байналмилалӣ гирад?', 'Рухсатномаҳо ба шахсони воқеӣ ва ҳуқуқии Ҷумҳурии Тоҷикистон дода мешаванд, ки ба ҳамлу нақли байналмилалӣ машғуланд.', 'permit', 'tg'),
                                                                     ('Барои ронанда ҳангоми сафар чӣ ҳуҷҷатҳо лозиманд?', 'Ҳуҷҷатҳое, ки тибқи қонунгузорӣ ва шартномаҳои байналмилалии Ҷумҳурии Тоҷикистон барои иҷрои ҳамлу нақли автомобилӣ заруранд, бояд дар дасти ронандагони воситаҳои нақлиёт бошанд: шиносномаи хориҷӣ, шаҳодатномаи ронандагӣ, китобчаи TIR-CARNET, шаҳодатномаи техникии воситаи нақлиёт, шаҳодатномаи бақайдгирӣ, маълумот оид ба иҷозати ҳамлу нақли байналмилалӣ, борхати молиявӣ, шаҳодатномаи ҳамлу нақли борҳои зудвайроншаванда, раводид, иҷозат барои транзит ва вуруд, суғурта.', 'permit', 'tg'),
                                                                     ('Раванди гирифтани рухсатнома чӣ гуна аст?', 'Раванд чунин аст: пешниҳоди дархост, баррасии ҳуҷҷатҳо, тасдиқи мувофиқат, додани рухсатнома бар асоси квота.', 'permit', 'tg'),
                                                                     ('Агар муҳлати рухсатнома гузарад, чӣ бояд кард?', 'Ба Хадамоти назорати давлатии соҳаи нақлиёт муроҷиат кунед ва барои гирифтани рухсатномаи нав дархост диҳед.', 'permit', 'tg'),
                                                                     ('Назорати вуруд, судур ва транзити нақлиёт тавассути кадом мақомот сурат мегирад?', 'Мақомотҳои назоратӣ: сарҳадӣ, гумрук, мақомоти нақлиёт, фитосанитарӣ ва байторӣ.', 'permit', 'tg'),
                                                                     ('Оё додани рухсатнома аз як интиқолдиҳанда ба дигар мумкин аст?', 'Не, ин қатъиян манъ аст.', 'permit', 'tg'),
                                                                     ('Варақаи рухсатнома кадом маълумотро дар бар мегирад?', 'Намуди ҳамлу нақл, силсила ва рақам, миқдори сафарҳо, муҳлат, маълумоти интиқолдиҳанда, ҷой ва имзои мақомот, хатсайр, маълумоти воситаи нақлиёт ва бор, вазн, мӯҳрҳои назоратӣ ва тавсияҳо.', 'permit', 'tg'),
                                                                     ('Намудҳои рухсатномаҳо кадомҳоянд?', 'Рухсатномаҳои Р-1, Р-3, Р-БП, СР-1, СР-2, СР-3, Р-З-ОГ бо мақсадҳои гуногун аз ҷумла воридот, содирот, транзит ва борҳои махсус.', 'permit', 'tg'),
                                                                     ('Ҳамлу нақли байналмилалӣ дар Тоҷикистон чӣ корҳоро дар бар мегирад?', 'Вуруд ва баромади воситаҳои нақлиёт, транзит, интиқоли борҳо, аз ҷумла борҳои хатарнок.', 'permit', 'tg'),
                                                                     ('Тартиби рафтуомад бо кадом санадҳо танзим мешавад?', 'Шартномаҳои байналмилалӣ, Қоидаҳои Оинномаи нақлиёти автомобилӣ, Низомнома оид ба иҷозатномадиҳӣ.', 'permit', 'tg'),
                                                                     ('Кадом воситаҳои нақлиёт метавонанд дар ҳамлу нақли байналмилалӣ истифода шаванд?', 'Автомобилҳои боркаш, автобусҳо ва сабукравҳо бо талаботи мушаххас.', 'permit', 'tg'),
                                                                     ('Бе иҷозатномаи зарурӣ фаъолият намудан чи ҷавобгарӣ дорад?', 'Мувофиқи моддаҳои 510 ва 511 Кодекси ҳуқуқвайронкунии маъмурӣ, ҷазо пешбинӣ шудааст.', 'permit', 'tg'),
                                                                     ('Муҳлати амалии рухсатнома чӣ гуна аст?', 'Барои як рафтуомад эътибор дорад.', 'permit', 'tg'),
                                                                     ('Контакты', 'Тел: 488888118', 'permit', 'tg');

